$(function() {

    var $wnd = $(window);
    var $top = $(".page-top");
    var $html = $("html, body");
    var $header = $(".section-header");
    var $menu = $(".main-menu");
    var $loader = $(".preloader");

    $wnd.on('load', function() {        
        // $loader.delay(0).fadeOut('slow');    
        
        $(".section-why .rect").equalHeights();
        $(".section-standart .item").equalHeights();
        $(".section-optima .item").equalHeights();
        $(".section-mono .item").equalHeights();
        $(".section-premium .item").equalHeights();
        $(".section-mix .item").equalHeights();
        $(".section-comfort .item").equalHeights();

        $(".section-optima").addClass('hidden');
        $(".section-mono").addClass('hidden');
        $(".section-premium").addClass('hidden');
        $(".section-mix").addClass('hidden');
        $(".section-comfort").addClass('hidden');

        $(".aksessuar").equalHeights();
    });

    $wnd.scroll(function() { onscroll(); });

    var menuTop = $menu.offset().top;

    var onscroll = function() {
        if($wnd.scrollTop() > menuTop) {
            $menu.addClass('fixed');
        } else {
            $menu.removeClass('fixed');
        }

        if($wnd.scrollTop() > $wnd.height()) {
            $top.addClass('active');
        } else {
            $top.removeClass('active');
        }

        var scrollPos = $wnd.scrollTop() + 88 + 20;

        $menu.find("a").each(function() {
            var link = $(this);
            var id = link.attr('href');
            var section = $(id);
            var sectionTop = section.offset().top;

            if(sectionTop <= scrollPos && (sectionTop + section.height()) >= scrollPos) {
                link.addClass('active');
            } else {
                link.removeClass('active');
            }
        });
    }

    onscroll();

    $top.click(function() {
        $html.stop().animate({ scrollTop: 0 }, 'slow', 'swing');
    });

    $hamburger = $(".hamburger");

    $hamburger.click(function() {
        toggleHamburger();
        return false;
    });  

    function toggleHamburger() {
        $this = $hamburger;
        if(!$this.hasClass("is-active")) {
            $this.addClass('is-active');
            $menu.slideDown('700');
        } else {
            $this.removeClass('is-active');
            $menu.slideUp('700');
        }
    }

    $(".main-menu a").click(function(e) {
        e.preventDefault();
        var $href = $(this).attr('href');
        if($href.length > 0) {
            var dh = $wnd.outerWidth() < 992 ? 83 : 35;
            var top = $href.length == 1 ? 0 : $($href).offset().top - dh;
            $html.stop().animate({ scrollTop: top }, "slow", "swing");
            // toggleHamburger();
        }
    });

    $('.select').selectize();    

    $(".modal-open").click(function() {
        var id = $(this).data('id');

        if(id == 'matras-modal') {
            var type = $(this).data('type');
            var index = $(this).data('index');
            openMatras(type, index);
        }

        $('#'+id).fadeIn(500);
        return false;
    });

    var $mmatras = $("#matras-modal");
    var props = ['title', 'layer', 'height', 'garranty', 'weight', 'price'];

    function openMatras(type, index) {
        var item = data[type]['items'][index];
        var imgSrc = "./img/" + type + (item['key'] ? "-" + item['key'] : "") + ".png";

        $mmatras.find(".modal-more-image img").attr("src", imgSrc);
        $mmatras.find(".modal-more-content").html(data[type]['content']);

        for(var i = 0; i < props.length; i++) {
            $mmatras.find(".modal-more-"+props[i]).html(item[props[i]]);
        }
    }

    $(".modal").click(function() {
        $(this).fadeOut(500);        

        var $phone = $(this).find('.phone');
        $phone.removeClass('error');
        $phone.val('');
    });

    $(".modal-content").click(function(e) {
        e.stopPropagation();
    });

    $(".modal-close").click(function() {
        $(this).closest('.modal').fadeOut(500);

        var $phone = $(this).siblings('.phone');
        $phone.removeClass('error');
        $phone.val('');
    });

    var $thanks = $("#thanks");

    $(".modal-submit").click(function(e) {
        e.preventDefault();
        
        var $form = $(this).closest('form');
        var $phone = $form.find('.phone');

        if($phone.length && !$phone.val()) {
            $phone.addClass('error');
        } else {
            $phone.removeClass('error');

            $.ajax({
                type: "POST",
                url: "/mail.php",
                data: $form.serialize()
            }).done(function() {
                $form[0].reset();
            });

            $(this).closest('.modal').fadeOut(500);
            $thanks.fadeIn(500);
        }
    });

    $(".phone").mask("+7 (999) 999 99 99", {
        completed: function() {
            $(this).removeClass('error');
        }
    });    

    var $tabs = $(".tab"),
        $matras = $(".matras");

    $tabs.click(function() {
        var $this = $(this);
        var id = $this.data('id');
        
        $tabs.removeClass('active');
        $this.addClass('active');

        $matras.addClass('hidden');
        $matras.each(function(i, item) {
            $item = $(item);
            if($item.data('id') == id) {
                $item.removeClass('hidden');
            }
        });
    });

    $("#carousel-team").owlCarousel({
        nav: true,
        dots: false,
        loop: true,
        smartSpeed: 500,
        itemsScaleUp:true,
        margin: 10,
        navText: ['', ''],
        onChanged: function(e) {
            setTimeout(function() {
                $(".carousel-team .owl-item").removeClass("scale-1 scale-2 scale-3 s-0 s-4");
                var items = $(".carousel-team .owl-item.active");
                if(items.length == 5) {
                    items.each(function(i, item) {
                        if(i == 0 || i == 4) $(item).addClass("scale-1 s-"+i)                        
                        if(i == 1 || i == 3) $(item).addClass("scale-2");
                        else if(i == 2) $(item).addClass("scale-3");                        
                    });
                } 
                else if(items.length == 3) {
                    items.each(function(i, item) {
                        if(i == 0 || i == 2) $(item).addClass("scale-2");
                        else $(item).addClass("scale-3");                        
                    });
                }
            }, 20);
        },
        responsive: {
            0: { items: 1 },
            768: { items: 2 },        
            992: { items: 3 },
            1200: { items: 5 }        
        },
    });

    $("#carousel-certificate").owlCarousel({
        nav: true,
        dots: false,
        loop: true,
        smartSpeed: 500,
        margin: 30,
        itemsScaleUp:true,
        navText: ['', ''],
        responsive: {
            0: { items: 1 },
            480: { items: 2 },
            768: { items: 3 },        
            992: { items: 4, margin: 50 },
            1200: { items: 4, margin: 100 }        
        },
    });

    $("#carousel-product").owlCarousel({
        items: 1,
        nav: true,
        dots: false,
        thumbs: true,
        thumbsPrerendered: true,
        loop: true,
        smartSpeed: 500,
        margin: 30,
        navText: ['', ''],
    });

});
    